import { defaultKeymap } from '@codemirror/commands';
import { keymap } from '@codemirror/view';
// --- END CodeMirror Imports ---


// --- Environment Variable Setup ---
const appId = process.env.REACT_APP_APP_ID || 'default-app-id';
const firebaseConfig = {
Â  apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
Â  authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
Â  projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
Â  storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET,
Â  messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
Â  appId: process.env.REACT_APP_FIREBASE_APP_ID,
Â  measurementId: process.env.REACT_APP_FIREBASE_MEASUREMENT_ID
};
const initialAuthToken = process.env.REACT_APP_INITIAL_AUTH_TOKEN || null;

// --- Firebase Initialization ---
let firebaseApp, db, auth;

// Validate Firebase config
const isValidConfig = firebaseConfig &&Â 
Â  firebaseConfig.apiKey &&Â 
Â  firebaseConfig.authDomain &&Â 
Â  firebaseConfig.projectId;

if (isValidConfig) {
Â  try {
Â  Â  firebaseApp = initializeApp(firebaseConfig);
Â  Â  db = getFirestore(firebaseApp);
Â  Â  auth = getAuth(firebaseApp);
Â  Â  console.log("Firebase initialized successfully");
Â  } catch (error) {
Â  Â  console.warn("Firebase initialization failed, falling back to local mode:", error);
Â  Â  // Clear Firebase instances to ensure we don't partially use them
Â  Â  firebaseApp = null;
Â  Â  db = null;
Â  Â  auth = null;
Â  }
} else {
Â  console.warn("Invalid Firebase config, running in local-only mode");
}

// --- Multi-Language Support Configuration ---
const LANGUAGE_CONFIGS = {
Â  Â  'React.js': {
Â  Â  Â  Â  icon: 'âš›ï¸',
Â  Â  Â  Â  label: 'React.js',
Â  Â  Â  Â  editable: true,
Â  Â  Â  Â  codemirrorLang: javascript({ jsx: true }),
Â  Â  Â  Â  files: {
Â  Â  Â  Â  Â  Â  // Note: index.js imports App and handles React rendering logic
Â  Â  Â  Â  Â  Â  '/src/index.js': { code: `import React from 'react';\nimport App from './App';\n\n// React entry point (Simulated).`, hidden: false, entry: true },
Â  Â  Â  Â  Â  Â  '/src/App.js': {
Â  Â  Â  Â  Â  Â  Â  Â  code: `import React, { useState } from 'react';\n\n// Main App Component\nconst App = () => {\nÂ  const [count, setCount] = useState(0);\n\nÂ  const increment = () => {\nÂ  Â  setCount(c => c + 1);\nÂ  };\n\nÂ  return (\nÂ  Â  <div className="flex flex-col items-center p-4 min-h-full">\nÂ  Â  Â  <h1 className="text-3xl font-bold mb-4 text-green-500">CipherStudio Live React App</h1>\nÂ  Â  Â  <p className="text-xl mb-6">Count: {count}</p>\nÂ  Â  Â  <button onClick={increment} className="bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded-lg transition duration-200 shadow-md">\nÂ  Â  Â  Â  Increment\nÂ  Â  Â  </button>\nÂ  Â  </div>\nÂ  );\n};\n\nexport default App;`,
Â  Â  Â  Â  Â  Â  Â  Â  hidden: false,
Â  Â  Â  Â  Â  Â  },
Â  Â  Â  Â  Â  Â  '/src/styles.css': { code: `/* Global styles for React app */\nbody { font-family: sans-serif; }`, hidden: false },
Â  Â  Â  Â  Â  Â  '/public/index.html': { code: '<!DOCTYPE html>...', hidden: true },
Â  Â  Â  Â  Â  Â  '/package.json': { code: '{"dependencies": {"react": "18.2.0"}}', hidden: true }
Â  Â  Â  Â  },
Â  Â  Â  Â  description: 'React environment. **Live rendering is now a static output simulation.**',
Â  Â  },
