Â  Â  },
};

// --- Helper Functions ---
const getLangInitialFiles = (language) => LANGUAGE_CONFIGS[language]?.files || LANGUAGE_CONFIGS['React.js'].files;
const getLanguageKeys = () => Object.keys(LANGUAGE_CONFIGS).sort();
const supportedConversionLangs = getLanguageKeys().filter(lang => lang !== 'React.js');


/**
Â * Hook to manage project files, persistence, and state.
Â */
const useProjectManager = (dbInstance, authInstance) => {Â 
Â  const [selectedLanguage, setSelectedLanguage] = useState('React.js');
Â  const [currentFiles, setCurrentFiles] = useState(getLangInitialFiles('React.js'));
Â  const [selectedPath, setSelectedPath] = useState('/src/App.js');
Â  const [currentProjectId, setCurrentProjectId] = useState(null);
Â  const [userId, setUserId] = useState(null);
Â  const [isAuthReady, setIsAuthReady] = useState(false);
Â  const [loading, setLoading] = useState(false);
Â  const [message, setMessage] = useState('');
Â  const [isAutosaveEnabled, setIsAutosaveEnabled] = useState(true);

Â  // 1. Authentication and User ID setup
Â  useEffect(() => {
Â  Â  const authenticate = async () => {
Â  Â  Â  if (!authInstance) {
Â  Â  Â  Â  // If no auth instance, use local-only mode with a generated ID
Â  Â  Â  Â  setUserId(crypto.randomUUID());
Â  Â  Â  Â  setIsAuthReady(true);
Â  Â  Â  Â  return;
Â  Â  Â  }

Â  Â  Â  try {
Â  Â  Â  Â  if (initialAuthToken) {
Â  Â  Â  Â  Â  await signInWithCustomToken(authInstance, initialAuthToken);
Â  Â  Â  Â  } else {
Â  Â  Â  Â  Â  // Try anonymous auth, fallback to local mode if it fails
Â  Â  Â  Â  Â  try {
Â  Â  Â  Â  Â  Â  await signInAnonymously(authInstance);
Â  Â  Â  Â  Â  } catch (anonError) {
Â  Â  Â  Â  Â  Â  console.warn("Anonymous auth disabled, using local mode:", anonError);
Â  Â  Â  Â  Â  Â  setUserId(crypto.randomUUID());
Â  Â  Â  Â  Â  Â  setIsAuthReady(true);
Â  Â  Â  Â  Â  Â  return;
Â  Â  Â  Â  Â  }
Â  Â  Â  Â  }
Â  Â  Â  Â  setUserId(authInstance.currentUser?.uid || crypto.randomUUID());
Â  Â  Â  Â  setIsAuthReady(true);
Â  Â  Â  } catch (error) {
Â  Â  Â  Â  console.warn("Authentication failed, using local mode:", error);
Â  Â  Â  Â  setUserId(crypto.randomUUID());
Â  Â  Â  Â  setIsAuthReady(true);
Â  Â  Â  }
Â  Â  };
Â  Â  authenticate();
Â  }, [authInstance]);

Â  // Firestore path helper
Â  const getProjectPath = (id) => doc(dbInstance, 'artifacts', appId, 'users', userId, 'projects', id);

Â  // 2. File content update logic
Â  const updateFileContent = useCallback((path, newCode) => {
Â  Â  setCurrentFiles(prevFiles => ({
Â  Â  Â  ...prevFiles,
Â  Â  Â  [path]: { ...prevFiles[path], code: newCode },
Â  Â  }));
Â  }, []);

Â  // 3. Language Switch Logic
Â  const switchLanguage = useCallback((lang) => {
Â  Â  setSelectedLanguage(lang);
Â  Â  setCurrentFiles(getLangInitialFiles(lang));
Â  Â  const newFiles = getLangInitialFiles(lang);
Â  Â  const newPath = Object.keys(newFiles).find(p => newFiles[p].entry) || Object.keys(newFiles).sort()[0];
Â  Â  setSelectedPath(newPath);
Â  Â  setCurrentProjectId(crypto.randomUUID());
Â  Â  setMessage(`Switched to ${lang}. Start a new project or save your work.`);
Â  }, []);


Â  // 4. Persistence Functions (Save/Load/New)
Â  const saveProject = useCallback(async (name) => {
Â  Â  if (!dbInstance || !userId || !currentProjectId) return;
Â  Â  setLoading(true);

Â  Â  try {
Â  Â  Â  const projectDocRef = getProjectPath(currentProjectId);
Â  Â  Â  const projectData = {
Â  Â  Â  Â  name: name || `Project ${currentProjectId}`,
Â  Â  Â  Â  language: selectedLanguage,
Â  Â  Â  Â  files: currentFiles,
Â  Â  Â  Â  createdAt: new Date().toISOString(),
Â  Â  Â  Â  updatedAt: new Date().toISOString(),
Â  Â  Â  Â  userId: userId,
Â  Â  Â  };

Â  Â  Â  await setDoc(projectDocRef, projectData, { merge: true });
Â  Â  Â  setMessage(`Project '${projectData.name}' saved successfully!`);
Â  Â  } catch (error) {
Â  Â  Â  console.error("Error saving project:", error);
Â  Â  Â  setMessage('Error saving project. Check console for details.');
Â  Â  } finally {
Â  Â  Â  setLoading(false);
Â  Â  Â  setTimeout(() => setMessage(''), 3000);
Â  Â  }
Â  }, [dbInstance, userId, currentProjectId, currentFiles, selectedLanguage, getProjectPath]);

Â  const loadProject = useCallback(async (projectId) => {
Â  Â  if (!dbInstance || !userId) return;
Â  Â  setLoading(true);
Â  Â  setMessage(`Loading project ${projectId}...`);

Â  Â  try {
Â  Â  Â  const projectDocRef = getProjectPath(projectId);
Â  Â  Â  const docSnap = await getDoc(projectDocRef);

Â  Â  Â  if (docSnap.exists()) {
Â  Â  Â  Â  const data = docSnap.data();
Â  Â  Â  Â Â 
Â  Â  Â  Â  const loadedLanguage = data.language || 'React.js';
Â  Â  Â  Â  setSelectedLanguage(loadedLanguage);

Â  Â  Â  Â  setCurrentFiles(data.files || getLangInitialFiles(loadedLanguage));Â 
Â  Â  Â  Â  setCurrentProjectId(projectId);
Â  Â  Â  Â Â 
Â  Â  Â  Â  const newPath = Object.keys(data.files).find(p => data.files[p].entry) || Object.keys(data.files).sort()[0] || '/src/App.js';
Â  Â  Â  Â  setSelectedPath(newPath);
Â  Â  Â  Â Â 
Â  Â  Â  Â  setMessage(`Project '${data.name}' loaded. Language: ${loadedLanguage}`);
Â  Â  Â  } else {
Â  Â  Â  Â  setMessage(`Project ID ${projectId} not found.`);
Â  Â  Â  }
Â  Â  } catch (error) {
Â  Â  Â  console.error("Error loading project:", error);
Â  Â  Â  setMessage('Error loading project. Check console for details.');
Â  Â  } finally {
Â  Â  Â  setLoading(false);
Â  Â  Â  setTimeout(() => setMessage(''), 3000);
Â  Â  }
Â  }, [dbInstance, userId, getProjectPath]);

Â  const newProject = useCallback(() => {
Â  Â  setCurrentFiles(getLangInitialFiles(selectedLanguage));
Â  Â  setSelectedPath(Object.keys(getLangInitialFiles(selectedLanguage)).find(p => getLangInitialFiles(selectedLanguage)[p].entry) || '/src/App.js');
Â  Â  setCurrentProjectId(crypto.randomUUID());
Â  Â  setMessage(`New ${selectedLanguage} project created. Use "Save" to persist.`);
Â  }, [selectedLanguage]);

Â  const deleteProject = useCallback(async (projectId) => {
Â  Â  if (!dbInstance || !userId) return;
Â  Â  setLoading(true);

Â  Â  try {
