			// Return a friendly message for callers
			return msg;
		}

		const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-09-2025:generateContent?key=${apiKey}`;

		const payload = {
			contents: [{ parts: [{ text: prompt }] }],
			config: {
				systemInstruction: systemInstruction
			},
		};

		let attempts = 0;
		const maxAttempts = 3;
		let lastError = null;

		while (attempts < maxAttempts) {
			try {
				const response = await fetch(apiUrl, {
					method: 'POST',
					headers: { 'Content-Type': 'application/json' },
					body: JSON.stringify(payload)
				});

				if (!response.ok) {
					// Handle 403 specifically: likely invalid/missing permission or key
					if (response.status === 403) {
						const errText = `API returned status 403 (forbidden). Check API key and permissions.`;
						console.warn(errText);
						// For terminal simulation, return a localized fallback string instead of throwing
						if (toolName === 'Terminal Simulation') {
							return `[Simulation Error] ${errText}`;
						}
						throw new Error(errText);
					}
					throw new Error(`API returned status ${response.status}`);
				}

				const result = await response.json();
				const text = result.candidates?.[0]?.content?.parts?.[0]?.text || "Could not generate content.";

				// Only reset the main AI tool panel state if it was an explicit tool call
				if (toolName !== 'Terminal Simulation') {
					setAiLoading(false);
					setAiOutput(text);
					setActiveRightPanel('ai_tools'); // Switch to the AI panel
					setAiToolMessage(`Output for ${toolName} on ${selectedPath}`);
				}

				return text;

			} catch (error) {
				lastError = error;
				attempts++;
				const delay = Math.pow(2, attempts) * 1000;
				console.warn(`Attempt ${attempts} failed. Retrying in ${delay / 1000}s...`, error);
				if (attempts < maxAttempts) {
					await new Promise(resolve => setTimeout(resolve, delay));
				}
			}
		}

		const finalError = `Failed to get a response after ${maxAttempts} attempts. Error: ${lastError?.message || 'Unknown error'}`;

		if (toolName !== 'Terminal Simulation') {
			setAiLoading(false);
			setAiOutput(finalError);
			setAiToolMessage(`Failed to run ${toolName}`);
		}

		// For terminal simulation callers, return the error text to be shown inline
		if (toolName === 'Terminal Simulation') {
			return `[Simulation Error] ${finalError}`;
		}

		throw new Error(finalError);

	}, [selectedPath]);


